  methods:{
    ...mapActions(['login']),                   //注入action
    _login(){
      let params = {
        mobile:this.mobile,
        pwd:this.password
      }
      this.login(params).
      then(()=>{
          this.$router.replace('/main/index');          //正确完成后进入主页
      })
      .catch((error)=>{                                 //错误则清空密码文本框
          //可以在这里对Error进行捕获，然后根据错误类型进行相对应的处理
